          ];
          const q = quotes[i] || { name: t.name, quote: t.quote };
          return { ...t, name: q.name, quote: q.quote };
        });

  const processLabel = lang === "es" ? "Metodología" : "Metodologia";

  return (
    <div className="app">
      {/* HEADER */}
      <header className="navbar">
        <div className="container nav-inner">
