          {/* Menú pegado hacia la derecha, junto a ES/CAT y Reservar */}
          <div className="nav-right">
            <nav id="primary-nav" className={`nav-links ${navOpen ? "open" : ""}`} aria-label="Navegación principal">
              {/* Enlace de Metodología visible solo en móvil mediante CSS para no alterar desktop */}
              <a href="#proceso" className="link-process">{processLabel}</a>
              <a href="#sobre-mi">{t.nav.about}</a>
              <a href="#servicios">{t.nav.services}</a>
